function fetchPost (store) {
  return store.dispatch('FETCH_POST', {
    id: store.state.route.params.id
  })
}

export default {
  name: 'item-view',
  data () {
    return {
      loading: true,
      editing: false,
      error: null
    }
  },
  computed: {
    item () {
      return this.$store.state.posts.single
    },
    authenticated () {
      return this.$store.state.auth.authenticated
    }
  },
  methods: {
    editPost: function () {
      if (this.editing == true) {
        this.updatePost()
      }
      this.editing =! this.editing
    },
    updatePost: function () {
      this.$store.dispatch('UPDATE_POST', {
        id: this.$store.state.route.params.id,
        title: this.item.title,
        content: this.item.content,
      }).then(message => console.log(message))
      .catch((err) => {
        fetchPost(this.$store)
        console.log(err)
        this.error = "You cannot edit this post."
      })
    },
    removePost: function () {
      this.$store.dispatch('REMOVE_POST', {
        id: this.$store.state.route.params.id
      }).then(message => this.$router.push({ path: '/' }))
      .catch((err) => {
        console.log(err)
        this.error = "You cannot remove this post."
      })
    }
  },
  // on the server, only fetch the item itself
  preFetch: fetchPost,
  // on the client, fetch everything
  beforeMount () {
    fetchPost(this.$store)
  }
}



