      return new Promise((resolve, reject) => {
        commit('LOGIN_REQUEST')
        app.authenticate({
          type: 'local',
          'email': email,
          'password': password
        }).then((result) => {
          commit('SET_PROFILE', result)
          commit('LOGIN_SUCCESS', result)
          resolve()
        }).catch((error) => {
          commit('LOGIN_FAILURE')
          reject(error)
        })
      })
    },

