      state.token = result.token
      state.isAuthenticating = false
      state.authenticated = true
    },

    LOGOUT: (state) => {
      state.token = null
      state.authenticated = false
      state.profile = null
    },

    SET_PROFILE: (state, result) => {
      state.profile = { email: result.data.email }
    }
  },

  getters: {
    isAuthenticated: (state) => {
